buttoncontrol.script:
function onButtonPress()
	if User.isHost() then
		SafetyToolkit.cardAck(getName())
	else
		SafetyToolkit.cardSubmit(getName())					
	end
end
function onInit()
	sName = getName();
	local num = SafetyToolkit.checkUnack(sName)
	if num > 0 or not User.isHost() then 
		setVisible(true);
	end
	setIcons(sName, sName);
end
